mapper.select:
      SELECT id, EIGHT_ID,DATA_REVIEW_TIME,TABLE_NAME,MODIFY_TYPE FROM TBL_EIGHT_REVIEW	WHERE FIRST_SECRETARY=0 AND TOWN_POVERTY_OFFICE=0 AND COUNTY_POVERTY_OFFICE=0 AND AAK110=#{helpNumber}
UNION
SELECT id, EIGHT_ID,DATA_REVIEW_TIME,TABLE_NAME,MODIFY_TYPE FROM TBL_EIGHT_REVIEW	WHERE FIRST_SECRETARY=1 AND TOWN_POVERTY_OFFICE=0 AND COUNTY_POVERTY_OFFICE=0 AND AAK110=#{helpNumber}
UNION
SELECT id, EIGHT_ID,DATA_REVIEW_TIME,TABLE_NAME,MODIFY_TYPE FROM TBL_EIGHT_REVIEW	WHERE FIRST_SECRETARY=1 AND TOWN_POVERTY_OFFICE=1 AND COUNTY_POVERTY_OFFICE=0 AND AAK110=#{helpNumber}
